sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue === oldValue) {
        return;
    }

    //Instantiate the GetEmailAddress script include
    var getEmailAddress = new GlideAjax('GetEmailAddress');
    getEmailAddress.addParam('sysparm_name', 'getEmail'); //specify the getEmail method
    getEmailAddress.addParam('sysparm_userID', g_form.getValue('u_requested_for')); //pass the requested for sys_id
    getEmailAddress.getXML(populateEmailField);

    function populateEmailField(response) {
        var emailFromSI = response.responseXML.documentElement.getAttribute('answer');
        g_form.clearValue('u_requested_email');
        g_form.setValue('u_requested_email', emailFromSI);

    }

}
